import {React,useState} from 'react';
import { useForm } from "react-hook-form";
import "./login.css";

export default function Login(){
    const[show_register,setRegister] = useState(false)
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);

    let registerEmployee = () => {
        if(!show_register){
            setRegister(true)
        }
        else{
            setRegister(false)
        }
    }

    return( 
        <div className={show_register === true ? 'h-350 d-block login' : 'd-block login'}>
            <h5>{show_register === true ? 'Sign Up' : 'Sign In'}</h5>
            <hr />
            <form className="mt-7px" onSubmit={handleSubmit()}>
                <div className="form-group">
                    <label className="text-left d-block">Employee ID</label>
                    <input className="form-control" {...register("exampleRequired",{required: true})} placeholder="Enter Employee ID"></input>
                </div>
                {errors.exampleRequired && <div className="text-danger fw-500">Employee ID is Required</div>}
                {
                    show_register === true ?
                        <div className="form-group">
                            <label className="text-left d-block">Employee Name</label>
                            <input {...register("exampleRequired", { required: true })} className="form-control" placeholder="Enter Employee Name"></input>
                        </div>
                    : null    
                }
                {errors.exampleRequired && <div className="text-danger fw-500">Employee Name is Required</div>}
                <button type="submit" className="btn btn-success btn-smm">
                    SUBMIT
                </button>
                <a href="javascript:;" className="fw-600 text-primary pointer d-block mt-7px" onClick={registerEmployee}>{ show_register ? 'Please Click here to Login' : 'Please Click here to Register'}</a>
            </form>
        </div>
    )
}
