import React from "react";
import { useForm } from "react-hook-form";
import Select from 'react-select';
import { Modal } from 'react-responsive-modal';
const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
]; 
export default function CreateChallenges(props){
    const { create_challenge, handleSubmit, reset, formState: { errors } } = useForm();
    const [selectedOption, setSelectedOption] = useState(null);
    const onSubmit = (data,e) => {
        console.log(data)
    }
    return(
        <div>
            <Modal open={props.open} classNames={{animationIn: 'customEnterAnimation',animationOut: 'customLeaveAnimation'}} animationDuration={500} center>
                <div className="modal-title">
                    <div>
                        <h5>CREATE CHALLENGE</h5>
                        <span className="mt-2" onClick={props.hideChallenge}>
                            <i className="fa fa-times-circle pointer text-white fs-20"></i>
                        </span>
                    </div>
                </div>
                <div className="modal-body">
                    <form className="mt-7px" onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-group mb-0">
                            <label className="text-left d-block">Title</label>
                            <input className="form-control" {...create_challenge("title",{required: true})} placeholder="Enter Title"></input>
                        </div>
                        {errors.title && <div className="text-danger text-left fw-500">Title is Required</div>}
                        <div className="form-group mb-0 mt-16px">
                            <label className="text-left d-block">Description</label>
                            <textarea {...create_challenge("description", {required: true})} className="form-control" placeholder="Enter Description"></textarea>
                        </div>
                        {errors.descripiton && <div className="text-danger text-left fw-500">Description is Required</div>}
                        <div className="form-group mb-0 mt-16px">
                            <label className="text-left d-block">Tags</label>
                            <Select {...create_challenge("tags", {required: true})}  defaultValue={selectedOption} onChange={setSelectedOption} options={options}/>
                        </div>    
                        {errors.tags && <div className="text-danger text-left fw-500">Tags is Required</div>}
                        <button type="submit" className="btn btn-success btn-smm mt-12px">
                            SAVE
                        </button>
                    </form>
                </div>
            </Modal>
       </div>     
    )
}
